const cluster = require('cluster');
const memshared = require('../lib');

if (cluster.isMaster) {
  console.log("is master!");
}


if (cluster.isWorker) {
  console.log("is worker!");
  memshared.set("key", "value");
  memshared.get("key", (err, value) => {
    console.log("received respose:", value);
  });
}
